mapper.insert:
INSERT
  INTO INGRE A
(
  A.INGRE_NO
, A.INGRE_NAME
, A.INGRE_PRICE
, A.INGRE_IMG
)
VALUES
(
  SEQ_INGRE_NO.NEXTVAL
, #{name}
, #{price}
, #{img}
)

mapper.update:

	UPDATE INGRE A
	
	  
	  	, A.INGRE_NAME = #{name}
	  		  
	  
	  	, A.INGRE_PRICE = #{price}
	  
	  
	  	, A.INGRE_IMG = #{img}
	    
	
WHERE A.INGRE_NO=${no}

mapper.delete:
DELETE 
  FROM INGRE A
 WHERE A.INGRE_NO = #{no}

mapper.insert:
INSERT
  INTO RECIPE A
(
  A.RECIPE_NO
, A.FOOD_NAME
, A.RECIPE_PRICE
, A.FOOD_PRICE
, A.FOOD_EXP
)
VALUES
(
  SEQ_RECIPE_NO.NEXTVAL
, #{name}
, #{recipePrice}
, #{foodPrice}
, #{exp}
)

		INSERT
		  INTO RECIPE_INGRE A
		(
		  A.RECIPE_NO
   		, A.INGRE_NO
		)
		VALUES
		(
		  (SELECT B.RECIPE_NO
		  	 FROM RECIPE B
		  	WHERE B.FOOD_NAME = #{recipe}
		  )
		, (SELECT C.INGRE_NO
		  	 FROM INGRE C
		  	WHERE C.INGRE_NAME = #{ingre}
		  )
		)

mapper.update:

	UPDATE RECIPE A
	
	  
	  	, A.FOOD_NAME = #{name}
	  		  
	  
	  	, A.RECIPE_PRICE = #{recipePrice}
	  
	  
	  	, A.FOOD_PRICE = #{foodPrice}
	   
	  
	  	, A.FOOD_EXP = #{exp}
	    
	
WHERE A.RECIPE_NO=${no}

mapper.delete:
DELETE 
  FROM RECIPE A
 WHERE A.RECIPE_NO = #{no}

DELETE 
  FROM RECIPE_INGRE A
 WHERE A.RECIPE_NO = ( SELECT B.RECIPE_NO
  	 	   				 FROM RECIPE B
  		  				WHERE B.FOOD_NAME = #{recipe}
  	   				 )
   AND A.INGRE_NO = ( SELECT C.INGRE_NO
  	 	   			    FROM INGRE C
  		  			   WHERE C.INGRE_NAME = #{ingre}
  	   				)

mapper.insert:
INSERT
  INTO STORE A
(
  A.STORE_LEVEL
, A.STORE_NAME
, A.STORE_PRICE
, A.MAX_RECIPE
, A.STORE_EXP
)
VALUES
(
  #{level}
, #{name}
, #{price}
, #{maxRecipe}
, #{exp}
)

mapper.update:

	UPDATE STORE A
	
	  
	  	, A.STORE_LEVEL = #{level}
	  		  
	  
	  	, A.STORE_NAME = #{name}
	  		  
	  
	  	, A.STORE_PRICE = #{price}
	  
	  
	  	, A.MAX_RECIPE = #{maxRecipe}
	   
	  
	  	, A.STORE_EXP = #{exp}
	    
	
WHERE A.STORE_LEVEL=${tempLevel}

mapper.delete:
DELETE 
  FROM STORE A
 WHERE A.STORE_LEVEL=${level}
