mapper.insert:
	  INSERT
        INTO PLAYER
      (
        PLAYER_NAME
      , PLAYER_ID
      , PLAYER_PASS
      , PLAYER_EMAIL
      , PLAYER_GOLD
      , PLAYER_EXP
      )
      VALUES
      (
        #{name}
      , #{id}
      , #{pass}
      , #{email}
      , DEFAULT
      , DEFAULT
      )

mapper.select:
SELECT
      A.PLAYER_NAME
    , A.PLAYER_ID
    , A.PLAYER_PASS
    , A.PLAYER_EMAIL
    , A.PLAYER_GOLD
    , A.PLAYER_EXP
  FROM PLAYER A
  
  
        A.PLAYER_ID=#{id}
          
  
     AND   A.PLAYER_PASS=#{pass}
  
  
     AND   A.PLAYER_EMAIL=#{email}
  
  
     AND   A.PLAYER_NAME=#{name}
